import React, { useState, useEffect, useCallback } from 'react';
import { useForm, SubmitHandler } from 'react-hook-form';
import { yupResolver } from '@hookform/resolvers/yup';
import * as yup from 'yup';
import toast from 'react-hot-toast';
import { PhotoIcon, XMarkIcon } from '@heroicons/react/24/outline';
import { productAPI } from '../services/api';
import { Product, ProductFormData } from '../types';

const schema = yup.object({
  name: yup.string().required('请输入商品名称'),
  description: yup.string().required('请输入商品描述'),
  category: yup.string().required('请选择商品分类'),
  price: yup.number().positive('价格必须大于0').required('请输入价格'),
  stock: yup.number().integer('库存必须是整数').min(0, '库存不能小于0').required('请输入库存'),
  deliveryType: yup.string().oneOf(['link', 'text', 'file', 'account']).required('请选择发货类型'),
  deliveryTemplate: yup.string().required('请输入发货模板'),
  tags: yup.array().of(yup.string()),
});

type FormData = yup.InferType<typeof schema>;

interface ProductModalProps {
  product?: Product | null;
  onClose: () => void;
  onSave: () => void;
}

const ProductModal: React.FC<ProductModalProps> = ({ product, onClose, onSave }) => {
  const [loading, setLoading] = useState(false);
  const [tags, setTags] = useState<string[]>([]);
  const [tagInput, setTagInput] = useState('');
  const [images, setImages] = useState<File[]>([]);
  const [imagePreviewUrls, setImagePreviewUrls] = useState<string[]>([]);
  const [existingImages, setExistingImages] = useState<string[]>([]);
  const [imagesToRemove, setImagesToRemove] = useState<string[]>([]);

  const categories = ['影视资源', '教育资源', '软件工具', '游戏资源', '音乐资源', '其他'];
  const deliveryTypes = [
    { value: 'link', label: '网盘链接' },
    { value: 'text', label: '文本内容' },
    { value: 'file', label: '文件下载' },
    { value: 'account', label: '账号密码' },
  ];

  const {
    register,
    handleSubmit,
    formState: { errors },
    reset,
    watch,
    setValue,
  } = useForm<FormData>({
    resolver: yupResolver(schema),
    defaultValues: {
      name: '',
      description: '',
      category: '',
      price: 0,
      stock: 0,
      deliveryType: 'link',
      deliveryTemplate: '',
      tags: [],
    },
  });

  const deliveryType = watch('deliveryType');

  useEffect(() => {
    if (product) {
      reset({
        name: product.name,
        description: product.description,
        category: product.category,
        price: product.price,
        stock: product.stock,
        deliveryType: product.deliveryType,
        deliveryTemplate: product.deliveryTemplate,
      });
      setTags(product.tags || []);
      setExistingImages(product.images || []);
    } else {
      reset({
        name: '',
        description: '',
        category: '',
        price: 0,
        stock: 0,
        deliveryType: 'link',
        deliveryTemplate: '',
      });
      setTags([]);
      setImages([]);
      setImagePreviewUrls([]);
      setExistingImages([]);
      setImagesToRemove([]);
    }
  }, [product, reset]);

  const handleAddTag = () => {
    if (tagInput.trim() && !tags.includes(tagInput.trim())) {
      setTags([...tags, tagInput.trim()]);
      setTagInput('');
    }
  };

  const handleRemoveTag = (tagToRemove: string) => {
    setTags(tags.filter(tag => tag !== tagToRemove));
  };

  // 处理图片上传
  const handleImageUpload = (e: React.ChangeEvent<HTMLInputElement>) => {
    const files = Array.from(e.target.files || []);
    if (files.length + images.length + existingImages.length > 5) {
      toast.error('最多只能上传5张图片');
      return;
    }

    const validFiles = files.filter(file => {
      const isValidType = file.type.startsWith('image/');
      const isValidSize = file.size <= 5 * 1024 * 1024; // 5MB
      if (!isValidType) {
        toast.error(`${file.name} 不是有效的图片文件`);
      }
      if (!isValidSize) {
        toast.error(`${file.name} 文件大小超过5MB`);
      }
      return isValidType && isValidSize;
    });

    setImages(prev => [...prev, ...validFiles]);

    const newPreviewUrls = validFiles.map(file => URL.createObjectURL(file));
    setImagePreviewUrls(prev => [...prev, ...newPreviewUrls]);
  };

  const removeNewImage = (index: number) => {
    const newImages = images.filter((_, i) => i !== index);
    const newPreviewUrls = imagePreviewUrls.filter((_, i) => i !== index);

    URL.revokeObjectURL(imagePreviewUrls[index]);

    setImages(newImages);
    setImagePreviewUrls(newPreviewUrls);
  };

  const removeExistingImage = (index: number) => {
    const imageToRemove = existingImages[index];
    setExistingImages(prev => prev.filter((_, i) => i !== index));
    setImagesToRemove(prev => [...prev, imageToRemove]);
  };

  const onSubmit: SubmitHandler<FormData> = async (data) => {
    setLoading(true);
    try {
      // 处理图片上传
      let formData: FormData | ProductFormData;

      if (images.length > 0 || imagesToRemove.length > 0) {
        // 如果有图片变更，使用 FormData
        const form = new FormData();

        // 添加基本字段
        Object.keys(data).forEach(key => {
          if (key !== 'images' && data[key as keyof FormData] !== undefined) {
            form.append(key, String(data[key as keyof FormData]));
          }
        });

        // 添加新图片
        images.forEach((file: File) => {
          form.append('images', file);
        });

        // 添加保留的现有图片
        const retainedImages = existingImages.filter(img => !imagesToRemove.includes(img));
        retainedImages.forEach(img => {
          form.append('existingImages', img);
        });

        // 添加标签
        tags.forEach(tag => {
          form.append('tags', tag);
        });

        formData = form;
      } else {
        // 如果没有图片变更，使用普通对象
        formData = {
          ...data,
          tags,
          images: existingImages
        };
      }

      let response;
      if (product) {
        response = await productAPI.updateProduct(product._id, formData);
      } else {
        response = await productAPI.createProduct(formData);
      }

      if (response.success) {
        toast.success(product ? '商品更新成功' : '商品创建成功');
        onSave();
      } else {
        toast.error(response.message || '操作失败');
      }
    } catch (error: any) {
      toast.error(error.response?.data?.message || '操作失败');
    } finally {
      setLoading(false);
    }
  };

  const getDeliveryTemplate = useCallback((type: string) => {
    switch (type) {
      case 'link':
        return '网盘链接: {link}\n提取码: {password}\n有效期: {expiry}';
      case 'text':
        return '内容: {content}\n说明: {description}';
      case 'file':
        return '文件名: {filename}\n下载链接: {download_url}';
      case 'account':
        return '账号: {username}\n密码: {password}\n说明: {note}';
      default:
        return '';
    }
  }, []);

  useEffect(() => {
    if (deliveryType) {
      const template = getDeliveryTemplate(deliveryType);
      setValue('deliveryTemplate', template);
    }
  }, [deliveryType, getDeliveryTemplate, setValue]);

  return (
    <div className="fixed inset-0 bg-gray-600 bg-opacity-50 overflow-y-auto h-full w-full z-50">
      <div className="relative top-20 mx-auto p-5 border w-11/12 md:w-3/4 lg:w-2/3 xl:w-1/2 shadow-lg rounded-md bg-white">
        <div className="mt-3">
          <div className="flex justify-between items-center mb-6">
            <h3 className="text-lg font-medium text-gray-900">
              {product ? '编辑商品' : '添加商品'}
            </h3>
            <button
              onClick={onClose}
              className="text-gray-400 hover:text-gray-600"
            >
              <XMarkIcon className="w-6 h-6" />
            </button>
          </div>

          <form onSubmit={handleSubmit(onSubmit)} className="space-y-6">
            {/* 商品图片上传 */}
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                商品图片 (最多5张)
              </label>

              <div className="grid grid-cols-2 md:grid-cols-3 lg:grid-cols-5 gap-4 mb-4">
                {existingImages.map((imageUrl, index) => (
                  <div key={`existing-${index}`} className="relative group">
                    <img
                      src={imageUrl}
                      alt={`商品图片 ${index + 1}`}
                      className="w-full h-24 object-cover rounded-lg border border-gray-300"
                    />
                    <button
                      type="button"
                      onClick={() => removeExistingImage(index)}
                      className="absolute -top-2 -right-2 bg-red-500 text-white rounded-full p-1 opacity-0 group-hover:opacity-100 transition-opacity"
                    >
                      <XMarkIcon className="w-4 h-4" />
                    </button>
                  </div>
                ))}

                {imagePreviewUrls.map((url, index) => (
                  <div key={`new-${index}`} className="relative group">
                    <img
                      src={url}
                      alt={`新图片 ${index + 1}`}
                      className="w-full h-24 object-cover rounded-lg border border-gray-300"
                    />
                    <button
                      type="button"
                      onClick={() => removeNewImage(index)}
                      className="absolute -top-2 -right-2 bg-red-500 text-white rounded-full p-1 opacity-0 group-hover:opacity-100 transition-opacity"
                    >
                      <XMarkIcon className="w-4 h-4" />
                    </button>
                  </div>
                ))}

                {(images.length + existingImages.length) < 5 && (
                  <label className="flex flex-col items-center justify-center w-full h-24 border-2 border-gray-300 border-dashed rounded-lg cursor-pointer bg-gray-50 hover:bg-gray-100">
                    <div className="flex flex-col items-center justify-center pt-2 pb-2">
                      <PhotoIcon className="w-6 h-6 text-gray-400 mb-1" />
                      <p className="text-xs text-gray-500">点击上传</p>
                    </div>
                    <input
                      type="file"
                      multiple
                      accept="image/*"
                      onChange={handleImageUpload}
                      className="hidden"
                    />
                  </label>
                )}
              </div>

              <p className="text-xs text-gray-500">
                支持 JPG、PNG、GIF 格式，单个文件不超过 5MB
              </p>
            </div>

            {/* 原有的表单字段保持不变 */}
            <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">
                  商品名称 *
                </label>
                <input
                  type="text"
                  {...register('name')}
                  className={`w-full px-3 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent ${
                    errors.name ? 'border-red-500' : 'border-gray-300'
                  }`}
                  placeholder="请输入商品名称"
                />
                {errors.name && (
                  <p className="text-red-500 text-sm mt-1">{errors.name.message}</p>
                )}
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">
                  商品分类 *
                </label>
                <select
                  {...register('category')}
                  className={`w-full px-3 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent ${
                    errors.category ? 'border-red-500' : 'border-gray-300'
                  }`}
                >
                  <option value="">请选择分类</option>
                  {categories.map(category => (
                    <option key={category} value={category}>{category}</option>
                  ))}
                </select>
                {errors.category && (
                  <p className="text-red-500 text-sm mt-1">{errors.category.message}</p>
                )}
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">
                  价格 (¥) *
                </label>
                <input
                  type="number"
                  step="0.01"
                  {...register('price', { valueAsNumber: true })}
                  className={`w-full px-3 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent ${
                    errors.price ? 'border-red-500' : 'border-gray-300'
                  }`}
                  placeholder="0.00"
                />
                {errors.price && (
                  <p className="text-red-500 text-sm mt-1">{errors.price.message}</p>
                )}
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">
                  库存 *
                </label>
                <input
                  type="number"
                  {...register('stock', { valueAsNumber: true })}
                  className={`w-full px-3 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent ${
                    errors.stock ? 'border-red-500' : 'border-gray-300'
                  }`}
                  placeholder="0"
                />
                {errors.stock && (
                  <p className="text-red-500 text-sm mt-1">{errors.stock.message}</p>
                )}
              </div>
            </div>

            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                商品描述 *
              </label>
              <textarea
                {...register('description')}
                rows={3}
                className={`w-full px-3 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent resize-none ${
                  errors.description ? 'border-red-500' : 'border-gray-300'
                }`}
                placeholder="请输入商品描述"
              />
              {errors.description && (
                <p className="text-red-500 text-sm mt-1">{errors.description.message}</p>
              )}
            </div>

            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                发货类型 *
              </label>
              <select
                {...register('deliveryType')}
                className={`w-full px-3 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent ${
                  errors.deliveryType ? 'border-red-500' : 'border-gray-300'
                }`}
              >
                {deliveryTypes.map(type => (
                  <option key={type.value} value={type.value}>{type.label}</option>
                ))}
              </select>
              {errors.deliveryType && (
                <p className="text-red-500 text-sm mt-1">{errors.deliveryType.message}</p>
              )}
            </div>

            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                发货模板 *
              </label>
              <textarea
                {...register('deliveryTemplate')}
                rows={4}
                className={`w-full px-3 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent resize-none font-mono text-sm ${
                  errors.deliveryTemplate ? 'border-red-500' : 'border-gray-300'
                }`}
                placeholder="请输入发货模板，使用 {变量名} 格式"
              />
              {errors.deliveryTemplate && (
                <p className="text-red-500 text-sm mt-1">{errors.deliveryTemplate.message}</p>
              )}
              <p className="text-gray-500 text-xs mt-1">
                使用大括号包围变量名，如：{'{link}'}, {'{password}'} 等
              </p>
            </div>

            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                商品标签
              </label>
              <div className="flex flex-wrap gap-2 mb-2">
                {tags.map((tag, index) => (
                  <span
                    key={index}
                    className="inline-flex items-center px-2 py-1 rounded-full text-xs font-medium bg-blue-100 text-blue-800"
                  >
                    {tag}
                    <button
                      type="button"
                      onClick={() => handleRemoveTag(tag)}
                      className="ml-1 text-blue-600 hover:text-blue-800"
                    >
                      ×
                    </button>
                  </span>
                ))}
              </div>
              <div className="flex">
                <input
                  type="text"
                  value={tagInput}
                  onChange={(e) => setTagInput(e.target.value)}
                  onKeyPress={(e) => e.key === 'Enter' && (e.preventDefault(), handleAddTag())}
                  className="flex-1 px-3 py-2 border border-gray-300 rounded-l-lg focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                  placeholder="输入标签后按回车或点击添加"
                />
                <button
                  type="button"
                  onClick={handleAddTag}
                  className="px-4 py-2 bg-gray-100 border border-l-0 border-gray-300 rounded-r-lg hover:bg-gray-200 focus:outline-none focus:ring-2 focus:ring-blue-500"
                >
                  添加
                </button>
              </div>
            </div>

            <div className="flex justify-end space-x-3 pt-6 border-t">
              <button
                type="button"
                onClick={onClose}
                className="px-4 py-2 border border-gray-300 rounded-lg text-gray-700 hover:bg-gray-50 focus:outline-none focus:ring-2 focus:ring-blue-500"
              >
                取消
              </button>
              <button
                type="submit"
                disabled={loading}
                className="px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-blue-500 disabled:opacity-50 disabled:cursor-not-allowed flex items-center"
              >
                {loading ? (
                  <>
                    <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-white mr-2"></div>
                    {product ? '更新中...' : '创建中...'}
                  </>
                ) : (
                  product ? '更新商品' : '创建商品'
                )}
              </button>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
};

export default ProductModal;
