export interface User {
  _id: string;
  username: string;
  email: string;
  role: 'admin' | 'merchant' | 'customer';
  status: 'active' | 'inactive' | 'pending';
  merchantInfo?: {
    companyName: string;
    contactPerson: string;
    phone: string;
    qq?: string;
    wechat?: string;
    businessLicense?: string;
    description?: string;
  };
  createdAt: string;
  updatedAt: string;
}

export interface Product {
  _id: string;
  name: string;
  description: string;
  category: string;
  price: number;
  stock: number;
  deliveryType: 'link' | 'text' | 'file' | 'account';
  deliveryTemplate: string;
  merchant: string | User;
  status: 'active' | 'inactive';
  images: string[]; // 添加图片字段
  tags: string[]; // 添加标签字段
  sales: number;
  createdAt: string;
  updatedAt: string;
}

export interface ProductFormData {
  name: string;
  description: string;
  category: string;
  price: number;
  stock: number;
  deliveryType: 'link' | 'text' | 'file' | 'account';
  deliveryTemplate: string;
  tags?: string[];
  status?: 'active' | 'inactive';
  images?: File[]; // 添加图片文件
  generatePickupCode?: boolean; // 是否生成提货码
  pickupCodeConfig?: {
    usageLimit?: number;
    expiresInDays?: number;
  };
}

// 添加提货码接口
export interface PickupCode {
  _id: string;
  code: string;
  productId: string;
  merchantId: string;
  isActive: boolean;
  usageLimit?: number;
  usedCount: number;
  expiresAt?: string;
  isAvailable: boolean;
  createdAt: string;
  updatedAt: string;
}

export interface Order {
  _id: string;
  orderNumber: string;
  product: Product | string;
  customer: {
    email?: string;
    phone?: string;
    name?: string;
  };
  totalAmount: number;
  quantity: number;
  status: 'pending' | 'completed' | 'cancelled' | 'expired';
  createdAt: string;
  updatedAt: string;
  pickedUpAt?: string;
  expiresAt: string;
}

export interface ApiResponse<T = any> {
  success: boolean;
  message: string;
  data?: T;
  error?: string;
  errors?: Array<{
    field?: string;
    message: string;
    value?: any;
  }>;
}

export interface LoginRequest {
  username: string; // 改为 username，支持邮箱或用户名登录
  password: string;
}

export interface RegisterRequest {
  username: string;
  email: string;
  password: string;
  role?: 'merchant';
  merchantInfo?: {
    companyName: string;
    contactPerson: string;
    phone: string;
    qq?: string;
    wechat?: string;
    businessLicense?: string;
    description?: string;
  };
}

export interface ProductFormData {
  name: string;
  description: string;
  category: string;
  price: number;
  stock: number;
  deliveryType: 'link' | 'text' | 'file' | 'account';
  deliveryTemplate: string;
  tags?: string[];
  status?: 'active' | 'inactive'; // 添加 status 字段
}

// 添加仪表板统计数据的具体类型定义
export interface AdminDashboardStats {
  totalUsers: number;
  totalMerchants: number;
  totalProducts: number;
  totalOrders: number;
  totalRevenue: number;
  recentOrders: Order[];
  topProducts: Product[];
}

export interface MerchantDashboardStats {
  totalProducts: number;
  totalOrders: number;
  totalRevenue: number;
  pendingOrders: number;
  recentOrders: Order[];
}

export interface DashboardStats {
  totalUsers?: number;
  totalMerchants?: number;
  totalProducts: number;
  totalOrders: number;
  totalRevenue: number;
  pendingOrders?: number;
  recentOrders: Order[];
  topProducts?: Product[];
}
